package Training;

public class Five {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum = 0;
		for (int i = 1; i <= 50; i++) {
			if (i % 2 != 0) {
				// System.out.println(i);
				sum += i;

			}
		}
		System.out.println(sum);

	}

}
